mapper.select:
select (@rowNum:=@rowNum+1) as rowNo,${KFFields} from(
select (@rowNum:=@rowNum+1) as rowNo,${KFFields} from(
select ${KFFields} from ${tableName}

	time between

   					 	  DATE_FORMAT(#{dStart}, '%Y-%m-%d %H:%T:%s') 

	and

   			 			  DATE_FORMAT(#{dEnd}  , '%Y-%m-%d %H:%T:%s') 


) A ,(Select @rowNum :=0) B
) C


   					   and mod(C.rowNo,#{interval}) = 1
