mapper.select:
select

from tb2_user
where loginname = #{loginname,jdbcType=VARCHAR} and password = #{password,jdbcType=VARCHAR}

select count(user_id) from tb2_user where status=0

    and loginname like "%"#{loginname}"%"


    and role_id=#{roleId}


    and last_login>=#{lastLoginStart}


    and last_login<=#{lastLoginEnd}

mapper.update:
update tb2_user set
loginname=#{loginname},
username=#{username},
role_id=#{roleId}
,password=#{password} 
where user_id=#{userId}
